<script setup>
//use auth layout

import { ref } from 'vue'
import { useRouter } from 'vue-router'
import { useUserStore } from '~/stores/user'
const router = useRouter()
const showRegister = ref(false)
const registerSuccess = ref(false)
const loading = ref(false)
const showPassword = ref(false)
const registerData = ref({
  nombre: '',
  apellido: '',
  email: '',
  password: ''
})
const {showError} = useModal()
const showForgot = ref(false)
const forgotEmail = ref('')
const forgotSuccess = ref(false)
const forgotLoading = ref(false)

const loginData = ref({ email: '', password: '' })
const loginLoading = ref(false)
const loginError = ref('')
const loginSuccess = ref(false)
const goToRegister = () => {
  router.push('/register')
}
async function handleRegister() {
  loading.value = true
  try {
    const userStore = useUserStore()
    await userStore.registerUser(registerData.value)
    registerSuccess.value = true
    setTimeout(() => {
      closeRegister()
    }, 2000)
  } catch (error) {
    showError(error.message || 'Error al registrar usuario')
  } finally {
    loading.value = false
  }
}
function closeRegister() {
  showRegister.value = false
  registerSuccess.value = false
  loading.value = false
  registerData.value = { nombre: '', apellido: '', email: '', password: '' }
  showPassword.value = false
}
function handleForgot() {
  forgotLoading.value = true
  setTimeout(() => {
    forgotLoading.value = false
    forgotSuccess.value = true
  }, 1200)
}
function closeForgot() {
  showForgot.value = false
  forgotSuccess.value = false
  forgotLoading.value = false
  forgotEmail.value = ''
}
const handleLogin = async () => {
  loginLoading.value = true
  loginError.value = ''
  const userStore = useUserStore()
  try {
    const data = await userStore.loginUser(loginData.value.email, loginData.value.password)
    if (data.access_token) {
      loginSuccess.value = true
      //get checkout route from local storage
      const checkingRoute = localStorage.getItem('checkingRoute')
      if (checkingRoute) {
        console.log('Redirigiendo a:', checkingRoute)
        localStorage.removeItem('checkingRoute') // Limpiar después de usar
        router.push(checkingRoute)
      } else {
        router.push('/')
      }
    } else {
      loginError.value = data.message || 'Credenciales incorrectas'
      showError('Credenciales incorrectas')
    }
  } catch (error) { 
    console.log(error, 'error en login component')
    loginError.value = error.message || 'Error de conexión con el servidor'
    showError(loginError.value||'Error de conexión con el servidor')
  } finally {
    loginLoading.value = false
  }

}
definePageMeta({
  layout: 'auth'
})
</script>
